import fire from '../config/firebaseConfig';
import { getStartDate } from './dates';
export default function getAssigned(dispatch, uid) {
  var db = fire.firestore();
  db.collection("tasks").where("volunteerUid", "==", uid).where("timestamp", ">", getStartDate()).where("collected", "==", false).get().then(function (querySnapshot) {
    var tasks = [];
    querySnapshot.forEach(function (doc) {
      var task = doc.data();
      task.id = doc.id;
      tasks.push(task);
    });
    dispatch({
      type: 'ASSIGNEDTASKS',
      tasks: tasks
    });
  });
}
