import _objectSpread from "@babel/runtime-corejs2/helpers/esm/objectSpread";
import fire from '../config/firebaseConfig';
export default function getAllRegionTasks(dispatch, region) {
  var db = fire.firestore();
  var tasks = [];
  db.collection("tasks").where("region", "==", region).get().then(function (querySnapshot) {
    querySnapshot.forEach(function (task) {
      tasks.push(_objectSpread({}, task.data(), {
        id: task.id
      }));
    });
    dispatch({
      type: 'GETALLREGIONTASKS',
      tasks: tasks
    });
  });
}
